int main()
{
	application app;
	const auto fct = app.create_default_factory();
	const rect_i l = { 100, 100, 400, 300 };
	shared_ptr<form> f = fct->create_form();
	slot_connection c = f->close += [&app] {	app.exit();	};
	shared_ptr<staggered> labels;

	auto root = make_shared<overlay>();
		root->add(fct->create_control<control>("background"));
		root->add(labels = make_shared<staggered>());
			labels->set_base_width(pixels(150));
			for (auto i = begin(c_lipsum); i != end(c_lipsum); ++i)
			{
				auto lbl = fct->create_control<label>("label");

				lbl->set_text(*i);
				lbl->set_halign(agge::align_center);
				labels->add(pad_control(lbl, 7, 7));
			}

	f->set_root(root);
	f->set_location(l);
	f->set_visible(true);
	
	app.run();
}
